import Todo from './Todo'
import './TodoList.css'

const TodoList = ({ todos }) => {

    const deleteTodo = (id) => {
        todos = todos.filter(todo => todo.id !== id) ;
    }
    const editTodo = () => {}

    return (
        <div className="grid">
            {todos.map((element) => {
                return (
                    <Todo key={element.id} task={element} deleteTodo={deleteTodo}/>
                )
            })}
        </div>
    )
}

export default TodoList
