import React, { useState, useEffect } from 'react'
import Navbar from '../components/Dashboard/Navbar'
import TodoList from '../components/Dashboard/TodoList'

const DashboardPage = ({ setAuthentication }) => {

    const [todoList, setTodoList] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [user, setUser] = useState({ name: "" });

    const token = localStorage.getItem("token")
    const url = "http://localhost:5000"

    const getUser = () => {
        fetch(
            url + "/",
            {
                method: 'GET',
                headers: {
                    'token': token,
                }
            }

        ).then((res) => {
            if (res.status === 200) return res.json();
            else if (res.status === 403) {
                localStorage.removeItem('token')
                history.replace('/')
            }

        }).then((data) => {
            setUser({ name: data.name })

        })
    }
    const getTodoList = () => {
        fetch(
            url + "/todo",
            {
                method: 'GET',
                headers: {
                    'token': token,
                }
            }

        ).then((res) => {
            if (res.status === 200) return res.json();
            else if (res.status === 403) {
                localStorage.removeItem('token')
                history.replace('/')
            }

        }).then((data) => {
            setTodoList(data.todos)


        })

    }

    const searchTask = (searchQuery) => {
        console.log(`Searching ${searchQuery}`)
        fetch(
            url + '/search?query=' + searchQuery.query,
            {
                method: "GET",
                headers:
                {
                    'Content-Type': "application/json",
                    "token": token
                }
            }).then((res) => {
                if (res.status === 200) return res.json()
                else if (res.status === 403) {
                    localStorage.removeItem('token')
                    history.replace('/')
                }
            }).then((data) => {
                setTodoList(data.searchResult)
            })
    }

    const deleteTodo = (id) => {

        setTodoList(todoList.filter(todo => todo.id !== id))

    }


    const dummy_task = [
        {
            name: 'Clean',
            description: 'I need to clean my house',
            date_created: '2021-07-24',
            important: false,
        },
        {
            name: 'Run',
            description: 'I need to go for a run',
            date_created: '2021-07-24',
            important: false,
        },
        {
            name: 'Clean',
            description: 'I need to clean my house',
            date_created: '2021-07-24',
            important: true,
        },
        {
            name: 'Run',
            description: 'I need to go for a run',
            date_created: '2021-07-24',
            important: false,
        },
        {
            name: 'Clean',
            description: 'I need to clean my house',
            date_created: '2021-07-24',
            important: true,
        },
        {
            name: 'Run',
            description: 'I need to go for a run',
            date_created: '2021-07-24',
            important: true,
        },
        {
            name: 'Clean',
            description: 'I need to clean my house',
            date_created: '2021-07-24',
            important: true,
        },
        {
            name: 'Run',
            description: 'I need to go for a run',
            date_created: '2021-07-24',
            important: true,
        },
        {
            name: 'Clean',
            description: 'I need to clean my house',
            date_created: '2021-07-24',
            important: true,
        },
        {
            name: 'Run',
            description: 'I need to go for a run',
            date_created: '2021-07-24',
            important: false,
        },
    ]

    useEffect(() => {
        setIsLoading(true);
        getUser();
        getTodoList();
        setIsLoading(false);
    }, [])
    if (isLoading) {
        return (
            <section>
                <p>Loading...</p>
            </section>
        );
    }
    else {
        return (
            <>
                <Navbar name={user.name} setAuthentication={setAuthentication} searchTask={searchTask} />
                {/* <Navbar name={"Nephy"} setAuthentication={setAuthentication} searchTask={searchTask} /> */}
                <TodoList todos={todoList} deleteTodo={deleteTodo} />

            </>
        )

    }
}

export default DashboardPage
