import './Todo.css'

const Todo = ({ task }) => {
    const [important, switchImportant] = useState(false);
    switchImportant(task.important)
    const markImportant = (id) => {
        fetch(() => {
            fetch(
                url + "/important/" + id,
                {
                    method: 'PUT',
                    headers: {
                        'token': token,
                    }
                }

            ).then((res) => {
                if (res.status === 200) return res.json();

            }).then(() => {
                switchImportant(!important)

            })

        })
    }

    return (

        <div className={`container ${important ? "important" : null}`} onDoubleClick={markImportant(task.id)}>
            <h3>{task.name}</h3>
            <p className="description">{task.description}</p>
            <div className="date">{task.date_created}</div>
        </div>

    )
}

export default Todo
