import React, { useState, useEffect } from 'react'
import { AiFillCaretUp } from "react-icons/ai";
import './Todo.css'

const Todo = ({ key, task }) => {
    const [important, switchImportant] = useState(false);
    const [active, setActive] = useState(false);

    const markImportant = (id) => {
        console.log(id)
        const token = localStorage.getItem("token")
        const url = "http://localhost:5000"


        fetch(
            url + "/important/" + id,
            {
                method: 'PUT',
                headers: {
                    'token': token,
                }
            }

        ).then((res) => {
            if (res.status === 200) return res.json();

        }).then(() => {
            switchImportant(!important)

        })


    }
    useEffect(() => {
        switchImportant(task.important);
    }, [])

    const rotateCaret = () => {
        console.log(active)
        setActive(!active)
    }
    return (

        <div className={`container ${important ? "important" : null}`} onDoubleClick={() => markImportant(task.id)}>
            <div className="header">
                <h3>{task.name}</h3>
                <div className={active? "menu-arrow-down":"menu-arrow-up" } onClick={rotateCaret}><AiFillCaretUp/></div>
            </div>

            <p className="description">{task.description}</p>
            <div className="date">{task.date_created}</div>
        </div>

    )
}

export default Todo
