import React, { useState, useEffect } from 'react'
import { AiFillCaretUp, AiFillCaretDown } from "react-icons/ai";
import './Todo.css'
import TodoMenu from './TodoMenu';

const Todo = ({ key, task, deleteTodo }) => {
    const [important, switchImportant] = useState(false);
    const [active, setActive] = useState(false);


    const markImportant = (id) => {
        const token = localStorage.getItem("token")
        const url = "http://localhost:5000"


        fetch(
            url + "/important/" + id,
            {
                method: 'PUT',
                headers: {
                    'token': token,
                }
            }

        ).then((res) => {
            if (res.status === 200) return res.json();

        }).then(() => {
            switchImportant(!important)

        })
    }
    useEffect(() => {
        switchImportant(task.important);
    }, [])

    const rotateCaret = () => {
        setActive(!active)
    }

    const _delete = () => {
        const url = "http://localhost:5000"
        const token = localStorage.getItem('token');
        fetch(
            url + "/todo/" + task.id,
            {
                method: 'DELETE',
                headers: {
                    'token': token,
                }
            }

        ).then((res) => {
            if (res.status === 200) return res.json();

        }).then(() => {
            deleteTodo(task.id);
        })
    }
    return (

        <div className={`container ${important ? "important" : null}`} onDoubleClick={() => markImportant(task.id)}>
            <div className="header">
                <h3>{task.name}</h3>
                <div onClick={rotateCaret}>{active ? <AiFillCaretDown /> : <AiFillCaretUp />}</div>

            </div>
            <div>
                {active ?
                    <TodoMenu deleteTodo={_delete}/> : null
                }
            </div>
            <p className="description">{task.description}</p>
            <div className="date">{task.date_created}</div>
        </div>

    )
}

export default Todo
